mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT m.mat_match_statement, TO_CHAR(m.mat_match_date, 'YY/MM/DD')as mat_match_date, m.mat_match_inout, m.mat_lot_no, m.com_material_code, c.com_material_name, i.mat_inout_quantity, CASE WHEN m.mat_match_inout = 'INOUT004' THEN (i.mat_inout_quantity + m.mat_match_qty) ELSE (i.mat_inout_quantity - m.mat_match_qty) END AS final_qty, m.mat_match_qty FROM com_material c, mat_inout i, mat_match m WHERE c.com_material_code = m.com_material_code AND m.mat_lot_no = i.mat_lot_no AND i.MAT_INOUT_GUBUN = 'INOUT002'
AND m.mat_match_date BETWEEN #{matMatchDate} AND #{matMatchEndDate}
AND c.com_material_code LIKE '%' || #{comMaterialCode} || '%'
AND m.mat_match_inout = #{matMatchInout}
ORDER BY mat_match_date ) a WHERE ROWNUM <= #{lastIndex} ) WHERE rnum >= #{firstIndex}

mapper.update:
UPDATE mat_inout
SET MAT_INOUT_QUANTITY = #{finalQuantity}
WHERE MAT_LOT_NO = #{matLotNo}
AND MAT_INOUT_GUBUN = 'INOUT002'

UPDATE mat_match
SET mat_match_qty = #{finalQuantity}
WHERE MAT_LOT_NO = #{matLotNo}
