mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT e.erp_material_order_code, e.com_material_code, c.com_material_name, c.com_material_size, c.com_material_unit, e.erp_material_order_qty, e.erp_vendor_code, e.erp_material_unit_price, d.com_code_detail_name, TO_CHAR(e.erp_material_order_date, 'YY/MM/DD')as erp_material_order_date, TO_CHAR(e.erp_material_request_date, 'YY/MM/DD')as erp_material_request_date FROM erp_material_order e, com_material c, com_code_detail d WHERE e.com_material_code = c.com_material_code AND e.erp_material_order_code NOT IN (SELECT erp_material_order_code FROM qua_material_chk) AND e.erp_vendor_code = d.com_code_detail_id AND e.erp_material_order_code LIKE '%' || UPPER(#{erpMaterialOrderCode}) || '%'
AND c.com_material_name LIKE '%' || #{comMaterialName} || '%'
ORDER BY e.erp_material_order_date ) A WHERE ROWNUM <= #{lastIndex} ) WHERE RNUM >= #{firstIndex}

SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT e.erp_material_order_code, e.com_material_code, c.com_material_name, c.com_material_size, c.com_material_unit, e.erp_material_order_qty, e.erp_vendor_code, e.erp_material_unit_price, d.com_code_detail_name, TO_CHAR(e.erp_material_order_date, 'YY/MM/DD')as erp_material_order_date, TO_CHAR(e.erp_material_request_date, 'YY/MM/DD')as erp_material_request_date FROM erp_material_order e, com_material c, com_code_detail d WHERE e.com_material_code = c.com_material_code AND e.erp_vendor_code = d.com_code_detail_id AND e.erp_material_order_code LIKE '%' || UPPER(#{erpMaterialOrderCode}) || '%'
AND c.com_material_name LIKE '%' || #{comMaterialName} || '%'
ORDER BY e.erp_material_order_date ) A WHERE ROWNUM <= #{lastIndex} ) WHERE RNUM >= #{firstIndex}
