mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT e.erp_material_order_code, e.com_material_code, c.com_material_name, e.erp_material_order_qty, e.erp_vendor_code, e.erp_material_unit_price, TO_CHAR(e.erp_material_order_date, 'YY/MM/DD')as erp_material_order_date, TO_CHAR(e.erp_material_request_date, 'YY/MM/DD')as erp_material_request_date FROM erp_material_order e, com_material c WHERE e.com_material_code = c.com_material_code AND e.erp_material_order_code LIKE '%' || UPPER(#{erpMaterialOrderCode}) || '%'
AND c.com_material_name LIKE '%' || #{comMaterialName} || '%'
ORDER BY com_material_code ) A WHERE ROWNUM <= #{lastIndex} ) WHERE RNUM >= #{firstIndex}
