mapper.insert:

INSERT INTO MAT_INOUT 
	( MAT_INOUT_STATEMENT
	  , COM_MATERIAL_CODE
	  , MAT_INOUT_DATE
	  , MAT_INOUT_GUBUN
	  , MAT_INOUT_QUANTITY
	  , MAT_LOT_NO
	  , COM_PROCESS_CODE
	  , MAT_INOUT_UNIT_PRICE
	  , MAT_INOUT_PRICE )
VALUES ( #{matInoutStatement}
	  , #{comMaterialCode}
	  , #{matInoutDate}
	  , #{matInoutGubun}
	  , #{matInoutQuantity}
	  , #{matLotNo}
	  , #{comProcessCode}
	  , #{matInoutUnitPrice}
	  , #{matInoutPrice} )

mapper.update:

UPDATE MAT_INOUT
SET MAT_INOUT_STATEMENT=#{matInoutStatement}
	, COM_MATERIAL_CODE=#{comMaterialCode}
	, MAT_INOUT_DATE=#{matInoutDate}
	, MAT_INOUT_GUBUN=#{matInoutGubun}
	, MAT_INOUT_QUANTITY=#{matInoutQuantity}
	, MAT_LOT_NO=#{matLotNo}
	, COM_PROCESS_CODE=#{comProcessCode}
	, MAT_INOUT_UNIT_PRICE=#{matInoutUnitPrice}
	, MAT_INOUT_PRICE=#{matInoutPrice}
			WHERE MAT_INOUT_STATEMENT=#{matInoutStatement}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT MAT_INOUT_STATEMENT , COM_MATERIAL_CODE , MAT_INOUT_DATE , MAT_INOUT_GUBUN , MAT_INOUT_QUANTITY , MAT_LOT_NO , COM_PROCESS_CODE , MAT_INOUT_UNIT_PRICE , MAT_INOUT_PRICE FROM MAT_INOUT WHERE 1=1
AND MAT_INOUT_STATEMENT = #{searchKeyword}
AND COM_MATERIAL_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY MAT_INOUT_STATEMENT DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM MAT_INOUT WHERE 1=1
AND MAT_INOUT_STATEMENT = #{searchKeyword}
AND COM_MATERIAL_CODE LIKE '%' || #{searchKeyword} || '%'
