mapper.insert:
DECLARE
	BEGIN
		INSERT INTO pro_order 
							( pro_order_code
                      		, pro_order_gubun
                    		, pro_order_qty
                     		, pro_order_rank
                     		, pro_order_date)
  		  VALUES(  #{proOrderCode}
  		  		 , #{proOrderGubun}
  		  		 , #{proOrderQty}
  		  		 , #{proOrderSeq}
  		  		 , #{proOrderDate}  );

		INSERT INTO pro_order_detail
								 (pro_order_detail_code
                           	 	, pro_order_code
                           		, pro_plan_detail_code
                           		, erp_product_deadline
                          		, pro_work_date
                          		, erp_customer_code
                          		, pro_order_expect_qty
                          		, pro_order_day_qty
                          		, pro_order_seq
                        		, pro_order_qty
                           		, erp_product_name
                        		, erp_product_code
                           		, pro_process_lot_no)
      		  VALUES(  #{proOrderDetailCode}
	  		  		 , #{proOrderCode}
	  		  		 , #{proPlanDetailCode}
	  		  		 , #{erpProductDeadline}
	  		  		 , #{proWorkDate}
	  		  		 , #{erpCustomerCode} 
	  		  		 , #{proOrderExpectQty}
	  		  		 , #{proOrderDayQty}
	  		  		 , #{proOrderSeq}
	  		  		 , #{proOrderQty}
	  		  		 , #{erpProductName}
	  		  		 , #{erpProductCode}
	  		  		 , #{proProcessLotNo}  );
	  		  		 
	  	UPDATE    pro_plan_detail
		  SET     pro_order_detail_code = #{proOrderDetailCode}
		  WHERE   pro_plan_detail_code = #{proPlanDetailCode} ;
	END;

mapper.update:

	UPDATE PRO_ORDER
	SET PRO_ORDER_CODE=#{proOrderCode}
		, PRO_ORDER_GUBUN=#{proOrderGubun}
		, PRO_ORDER_QTY=#{proOrderQty}
		, PRO_ORDER_RANK=#{proOrderRank}
		, PRO_ORDER_DATE=#{proOrderDate}
				WHERE PRO_ORDER_CODE=#{proOrderCode}

