mapper.insert:

	INSERT INTO PRO_ORDER 
		( PRO_ORDER_CODE
		  , PRO_ORDER_GUBUN
		  , PRO_ORDER_QTY
		  , PRO_ORDER_RANK
		  , PRO_ORDER_DATE )
	VALUES ( #{proOrderCode}
		  , #{proOrderGubun}
		  , #{proOrderQty}
		  , #{proOrderRank}
		  , #{proOrderDate} )

mapper.update:

	UPDATE PRO_ORDER
	SET PRO_ORDER_CODE=#{proOrderCode}
		, PRO_ORDER_GUBUN=#{proOrderGubun}
		, PRO_ORDER_QTY=#{proOrderQty}
		, PRO_ORDER_RANK=#{proOrderRank}
		, PRO_ORDER_DATE=#{proOrderDate}
				WHERE PRO_ORDER_CODE=#{proOrderCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT od.erp_product_code , od.erp_product_name , om.pro_order_gubun , p.erp_order_code , TO_CHAR(od.erp_product_deadline, 'yyyy/MM/dd') as erp_product_deadline , p.erp_order_qty , od.pro_order_qty , m.mac_hour_qty , od.pro_order_day_qty , od.pro_order_expect_qty , TO_CHAR(od.pro_work_date, 'yyyy-MM-dd') as pro_work_date , od.pro_order_seq , od.pro_order_code FROM pro_order_detail od , pro_order om , pro_plan_detail p , mac m WHERE od.pro_order_code = om.pro_order_code AND od.pro_plan_detail_code = p.pro_plan_detail_code AND od.erp_product_code = m.com_product_code AND od.pro_work_date LIKE '%' || TO_DATE(#{proWorkDate}) || '%'
AND od.pro_order_code LIKE '%' || #{proOrderCode} || '%'
ORDER BY od.pro_plan_detail_code DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM >= #{firstIndex}

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(d.pro_work_date, 'yyyy/MM/dd') as pro_work_date , d.pro_order_code , d.erp_customer_code , d.erp_product_code , d.erp_product_name , p.erp_order_code , TO_CHAR(d.erp_product_deadline, 'yyyy/MM/dd') as erp_product_deadline , p.erp_order_qty , d.pro_order_qty , m.pro_order_gubun , l.pro_material_lot , d.pro_order_seq , d.pro_process_lot_no , m.pro_order_date FROM pro_order_detail d , pro_order m , pro_plan_detail p , pro_material_lot l WHERE d.pro_order_code = m.pro_order_code AND d.pro_plan_detail_code = p.pro_plan_detail_code AND d.pro_order_detail_code = l.pro_order_detail_code
AND d.pro_work_date BETWEEN #{startDate} AND #{endDate}
AND d.erp_product_deadline BETWEEN #{startDate} AND #{endDate}
AND d.erp_product_code LIKE '%' || #{erpProductCode} || '%'
AND d.erp_customer_code LIKE '%' || #{erpCustomerCode} || '%'
ORDER BY m.PRO_ORDER_CODE DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM >= #{firstIndex}

SELECT COUNT(*) totcnt FROM pro_order_detail o , pro_plan_detail p , mac m WHERE p.erp_product_code = o.erp_product_code AND m.com_product_code = o.erp_product_code
AND PRO_ORDER_CODE = #{searchKeyword}
AND PRO_ORDER_GUBUN LIKE '%' || #{searchKeyword} || '%'
