mapper.delete:

	DELETE FROM PRO_PROCESS 
				WHERE COM_PROCESS_CODE=#{comProcessCode}
						AND PRO_ORDER_DETAIL_CODE=#{proOrderDetailCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT COM_PROCESS_CODE , PRO_ORDER_DETAIL_CODE , COM_PRODUCT_F_CODE , PRO_PROCESS_F_QTY , MAC_LINE_NO , PRO_PROCESS_START_TIME , PRO_PROCESS_END_TIME , ERP_EMPLOYEE_ID , MAC_CODE , PRO_PROCESS_LOT_NO , PRO_PROCESS_QUANTITY FROM PRO_PROCESS WHERE 1=1
AND COM_PROCESS_CODE = #{searchKeyword}
AND PRO_ORDER_DETAIL_CODE LIKE '%' || #{searchKeyword} || '%'
ORDER BY COM_PROCESS_CODE DESC , PRO_ORDER_DETAIL_CODE DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM PRO_PROCESS WHERE 1=1
AND COM_PROCESS_CODE = #{searchKeyword}
AND PRO_ORDER_DETAIL_CODE LIKE '%' || #{searchKeyword} || '%'

SELECT 	erp_product_name
FROM 	pro_order_detail
WHERE 	pro_order_detail_code = #{proOrderDetailCode}

mapper.insert:
	DECLARE
		BEGIN
		INSERT INTO PRO_PROCESS 
			( COM_PROCESS_CODE
			  , PRO_ORDER_DETAIL_CODE
			  , MAC_LINE_NO
			  , PRO_PROCESS_START_TIME
			  , ERP_EMPLOYEE_ID
			  , MAC_CODE
			  , ERP_PRODUCT_NAME)
		VALUES ( #{comProcessCode}
			  , #{proOrderDetailCode}
			  , #{macLineNo}
			  , #{proProcessStartTime}
			  , #{erpEmployeeId}
			  , #{macCode} 
			  , #{erpProductName});

		INSERT INTO PRO_PROCESS 
			( COM_PROCESS_CODE
			  , PRO_ORDER_DETAIL_CODE
			  , ERP_PRODUCT_NAME)
		VALUES ( 'PROCG002'
			  , #{proOrderDetailCode}
			  , #{erpProductName});
			  
		INSERT INTO PRO_PROCESS 
			( COM_PROCESS_CODE
			  , PRO_ORDER_DETAIL_CODE
			  , ERP_PRODUCT_NAME)
		VALUES ( 'PROCG003'
			  , #{proOrderDetailCode}
			  , #{erpProductName});
			  
		UPDATE PRO_ORDER_DETAIL
		SET PRO_PROCESS_STATUS = 1
		WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
		
	END;

mapper.update:
DECLARE
	BEGIN
		UPDATE PRO_PROCESS
		SET	PRO_PROCESS_START_TIME=#{proProcessStartTime},
			MAC_LINE_NO=#{macLineNo},
			ERP_EMPLOYEE_ID=#{erpEmployeeId},
			MAC_CODE=#{macCode}
		WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode}
		AND COM_PROCESS_CODE = #{comProcessCode};
		
	
		UPDATE PRO_ORDER_DETAIL
		SET PRO_PROCESS_STATUS = 3
		WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
	
	
		UPDATE PRO_ORDER_DETAIL
		SET PRO_PROCESS_STATUS = 5
		WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
	
	
END;

		
       		 SELECT PRO_PROCESS_LOT_NO 
       		 FROM PRO_ORDER_DETAIL
       		 WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode}
   		    
		DECLARE
			BEGIN
				UPDATE PRO_PROCESS
				SET	PRO_PROCESS_END_TIME=#{proProcessEndTime},
					PRO_PROCESS_QUANTITY=#{proProcessQuantity},
					ERP_PRODUCT_NAME = #{comProductName}
				,
					PRO_PROCESS_LOT_NO = #{proProcessLotNo}
				
				WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode}
				AND COM_PROCESS_CODE = #{comProcessCode};
				
				
				UPDATE PRO_ORDER_DETAIL
				SET PRO_PROCESS_STATUS = 2
				WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
				
				
				UPDATE PRO_ORDER_DETAIL
				SET PRO_PROCESS_STATUS = 4
				WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
				
				
				UPDATE PRO_ORDER_DETAIL
				SET PRO_PROCESS_STATUS = 6
				WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};
				
		END;


	DECLARE
		BEGIN
			UPDATE PRO_PROCESS
			SET COM_PROCESS_CODE=#{comProcessCode}
				, PRO_PROCESS_END_TIME=#{proProcessEndTime}
			WHERE PRO_ORDER_DETAIL_CODE=#{proOrderDetailCode};
			
			UPDATE PRO_ORDER_DETAIL
			SET PRO_PROCESS_STATUS = 4
			WHERE PRO_ORDER_DETAIL_CODE = #{proOrderDetailCode};


UPDATE PRO_PROCESS
SET COM_PROCESS_CODE=#{comProcessCode}
		, MAC_LINE_NO=#{macLineNo}
		, PRO_PROCESS_START_TIME=#{proProcessStartTime}
		, ERP_EMPLOYEE_ID=#{erpEmployeeId}
		, MAC_CODE=#{macCode}
WHERE PRO_ORDER_DETAIL_CODE=#{proOrderDetailCode}
