mapper.insert:

	INSERT INTO PRO_ORDER 
		( PRO_ORDER_CODE
		  , PRO_ORDER_GUBUN
		  , PRO_ORDER_QTY
		  , PRO_ORDER_RANK
		  , PRO_ORDER_DATE )
	VALUES ( #{proOrderCode}
		  , #{proOrderGubun}
		  , #{proOrderQty}
		  , #{proOrderRank}
		  , #{proOrderDate} )

mapper.update:

	UPDATE PRO_ORDER
	SET PRO_ORDER_CODE=#{proOrderCode}
		, PRO_ORDER_GUBUN=#{proOrderGubun}
		, PRO_ORDER_QTY=#{proOrderQty}
		, PRO_ORDER_RANK=#{proOrderRank}
		, PRO_ORDER_DATE=#{proOrderDate}
				WHERE PRO_ORDER_CODE=#{proOrderCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT PRO_ORDER_CODE , PRO_ORDER_GUBUN , PRO_ORDER_QTY , PRO_ORDER_RANK , PRO_ORDER_DATE FROM PRO_ORDER WHERE 1=1
AND PRO_ORDER_CODE = #{searchKeyword}
AND PRO_ORDER_GUBUN LIKE '%' || #{searchKeyword} || '%'
ORDER BY PRO_ORDER_CODE DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM PRO_ORDER WHERE 1=1
AND PRO_ORDER_CODE = #{searchKeyword}
AND PRO_ORDER_GUBUN LIKE '%' || #{searchKeyword} || '%'
