mapper.insert:

	INSERT INTO PRO_PLAN 
		( PRO_PLAN_CODE
		  , PRO_PLAN_NAME
		  , PRO_PLAN_DATE
		  , ERP_CUSTOMER_CODE )
	VALUES ( #{proPlanCode}
		  , #{proPlanName}
		  , #{proPlanDate}
		  , #{erpCustomerCode} )

mapper.update:

	UPDATE PRO_PLAN
	SET PRO_PLAN_CODE=#{proPlanCode}
		, PRO_PLAN_NAME=#{proPlanName}
		, PRO_PLAN_DATE=#{proPlanDate}
		, ERP_CUSTOMER_CODE=#{erpCustomerCode}
				WHERE PRO_PLAN_CODE=#{proPlanCode}

mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT TO_CHAR(m.pro_plan_date, 'yyyy/MM/dd') as pro_plan_date , e.erp_customer_code , m.pro_plan_code , e.erp_product_code , e.erp_product_name , e.erp_order_code , TO_CHAR(e.erp_product_deadline, 'yyyy/MM/dd') as erp_product_deadline , e.erp_order_qty , d.pro_plan_qty , TO_CHAR(d.pro_work_date, 'yyyy/MM/dd') as pro_work_date , d.pro_plan_seq
FROM pro_plan_detail d , erp_product_order e , pro_plan m WHERE d.erp_order_code = e.erp_order_code AND m.pro_plan_code = d.pro_plan_code
AND m.pro_plan_date BETWEEN #{startDate} AND #{endDate}
AND e.erp_product_deadline BETWEEN #{startDate} AND #{endDate}
AND e.erp_product_code LIKE '%' || #{erpProductCode} || '%'
AND e.erp_customer_code LIKE '%' || #{erpCustomerCode} || '%'
ORDER BY m.PRO_PLAN_CODE DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM >= #{firstIndex}

SELECT COUNT(*) totcnt FROM pro_plan m , pro_plan_detail d , erp_product_order e WHERE m.pro_plan_code = d.pro_plan_code AND d.erp_order_code = e.erp_order_code AND 1=1
AND PRO_PLAN_CODE = #{searchKeyword}
AND PRO_PLAN_NAME LIKE '%' || #{searchKeyword} || '%'

SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT erp_order_code , erp_product_code , erp_product_name , erp_order_qty FROM erp_product_order WHERE 1=1 AND erp_product_code LIKE '%' || #{erpProductCode} || '%'
AND erp_product_name LIKE '%' || #{erpProductName} || '%'
ORDER BY erp_product_code
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM >= #{firstIndex}

SELECT COUNT(*) totcnt FROM erp_product_order WHERE 1=1
AND erp_product_code = #{searchKeyword}
AND erp_product_name LIKE '%' || #{searchKeyword} || '%'

SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
				SELECT COM_PRODUCT_CODE,
						COM_PRODUCT_NAME,
						COM_PRODUCT_SIZE
				FROM COM_PRODUCT
				WHERE 1=1
				 AND
					COM_PRODUCT_CODE = #{comProductCode}
				
				AND
					COM_PRODUCT_NAME LIKE '%' || #{comProductName} || '%'
				
				ORDER BY 
						COM_PRODUCT_NAME
							
	) A WHERE ROWNUM <= #{lastIndex}
)WHERE RNUM >= #{firstIndex}
