mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT DISTINCT q.qua_material_statement, q.erp_material_order_code, q.qua_material_chk_date, q.erp_vendor_code, d.com_code_detail_name, e.erp_material_order_qty, e.erp_material_unit_price, e.erp_material_order_qty * e.erp_material_unit_price as erp_material_price, q.qua_material_f_qty
FROM qua_material_chk q, erp_material_order e, com_code_detail d, qua_material_f f
WHERE q.qua_material_statement = f.qua_material_statement AND q.erp_material_order_code = e.erp_material_order_code AND q.erp_material_order_code = f.erp_material_order_code AND q.erp_vendor_code = d.com_code_detail_id
AND q.erp_vendor_code = #{erpVendorCode}
AND q.erp_material_order_code = #{erpMaterialOrderCode}
AND e.com_material_code = #{comMaterialCode}
AND q.qua_material_chk_date = #{quaMaterialChkDate}
ORDER BY qua_material_chk_date ) a WHERE ROWNUM <= #{lastIndex} ) WHERE rnum >= #{firstIndex}

SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT f.erp_material_order_code, f.com_material_code, c.com_material_name, f.com_material_f_code, m.com_material_f_name, f.qua_material_f_qty, f.qua_material_statement, e.erp_material_unit_price
FROM qua_material_f f, qua_material_chk q, com_material c, com_material_f m, erp_material_order e
WHERE f.erp_material_order_code = q.erp_material_order_code AND f.com_material_code = c.com_material_code AND f.com_material_f_code = m.com_material_f_code AND f.erp_material_order_code = e.erp_material_order_code AND q.qua_material_statement = f.qua_material_statement
AND e.erp_vendor_code = #{erpVendorCode}
AND q.erp_material_order_code = #{erpMaterialOrderCode}
AND e.com_material_code = #{comMaterialCode}
AND e.erp_material_request_date = #{erpMaterialRequestDate}
ORDER BY qua_material_chk_date ) a WHERE ROWNUM <= #{lastIndex} ) WHERE rnum >= #{firstIndex}
