mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM RNUM FROM ( SELECT SAL_MATCH_STATEMENT, SAL_MATCH_INOUT, TO_CHAR(SAL_MATCH_DATE, 'YYYY/MM/dd') as SAL_MATCH_DATE, S.COM_PRODUCT_CODE, S.PRO_PROCESS_LOT_NO, TO_CHAR(S.SAL_WRITE_DATE, 'YYYY/MM/dd') as SAL_WRITE_DATE, C.COM_PRODUCT_NAME, I.SAL_INOUT_QUANTITY, SAL_MATCH_QTY, (I.SAL_INOUT_QUANTITY - S.SAL_MATCH_QTY) as FINAL_QUANTITY FROM SAL_MATCH S, SAL_INOUT I, COM_PRODUCT C WHERE 1=1 AND S.COM_PRODUCT_CODE = C.COM_PRODUCT_CODE AND S.PRO_PROCESS_LOT_NO = I.PRO_PROCESS_LOT_NO AND I.SAL_INOUT_GUBUN = 'INOUT003' AND SAL_MATCH_DATE = #{salMatchDate}
AND SAL_MATCH_INOUT = #{salMatchInout}
AND S.COM_PRODUCT_CODE LIKE '%' || #{comProductCode} || '%'
AND S.PRO_PROCESS_LOT_NO LIKE '%' || #{proProcessLotNo} || '%'
ORDER BY SAL_MATCH_DATE DESC
) A WHERE ROWNUM <= #{lastIndex} )WHERE RNUM >= #{firstIndex}

mapper.update:
UPDATE SAL_MATCH SET
SAL_MATCH_DATE = #{salMatchDate}
, SAL_MATCH_INOUT = #{salMatchInout}
, SAL_MATCH_QTY = #{salMatchQty}
, COM_PRODUCT_CODE = #{comProductCode}
, PRO_PROCESS_LOT_NO = #{proProcessLotNo}
, SAL_WRITE_DATE = #{salWriteDate}
WHERE SAL_MATCH_STATEMENT = #{salMatchStatement}

mapper.select:
SELECT COUNT(*) totcnt FROM SAL_MATCH WHERE 1=1
AND SAL_MATCH_STATEMENT = #{searchKeyword}
AND SAL_MATCH_INOUT LIKE '%' || #{searchKeyword} || '%'
